example.code:
$(".block").draggable({helper: 'clone'});
$(".drop").droppable({
	accept: ".block",
	activeClass: 'droppable-active',
	hoverClass: 'droppable-hover',
	drop: function(ev, ui) {
		$(this).append("<br>Dropped!");
	}
});

